<template>
  <!-- v-if="!$route.path.includes('preview-question')" -->

  <div>
    <div
      v-if="question && Object.keys(question).length > 0"
      class="card-main-wrapper rounded-5"
    >
      <section class="card-bottom-main-body">
        <div class="option-list-main" v-if="!$route.query.tour">
          <div
            v-if="showDrop"
            :data-toggle="data_toggle"
            class="three-circles avatar"
          >
            <div
              :data-toggle="data_toggle"
              class="icon icon-ellipsis-h color-mid-blue"
            ></div>
          </div>
          <ul
            v-if="$route.path.includes('preview-question')"
            :class="[
              is_card_btn_active
                ? `option-select-wrapper box-shadow-effect rounded-5`
                : `close-option-select-wrapper`,
            ]"
          >
            <li
              @click="$emit('editQuestion', question.id)"
              class="create-question-item"
            >
              Edit
            </li>
            <li
              @click="$emit('deleteQuestion', question.id)"
              class="create-question-item"
            >
              Remove
            </li>
          </ul>
          <ul
            v-else
            :class="[
              is_card_btn_active
                ? `option-select-wrapper box-shadow-effect rounded-5`
                : `close-option-select-wrapper`,
            ]"
          >
            <li
              v-if="question.owner === 1"
              @click="$emit('editQuestion', question.id)"
              class="create-question-item"
            >
              Edit Question
            </li>
            <li
              @click="$emit('deleteQuestion', question.id)"
              class="create-question-item"
            >
              Remove from Assessment
            </li>
            <li
              @click="
                $emit('view-details', isIndexed ? index + 1 : question.id)
              "
              class="create-question-item"
            >
              View Full Details
            </li>
          </ul>
        </div>

        <div
          v-if="question.comprehension"
          class="
            comprehension-passage
            d-flex
            flex-column
            justify-content-center
          "
          :class="
            !showMore && question.comprehension.body.length > 520
              ? 'align-items-center'
              : 'align-items-start'
          "
        >
          <div class="main">
            <div
              class="comprehension-text"
              :class="
                !showMore && question.comprehension.body.length > 520
                  ? 'hide'
                  : ''
              "
              v-html="
                question.comprehension.body.replace(
                  /(<p[^>]+?>|<p>|<\/p>)/gim,
                  ''
                )
              "
            ></div>
            <div
              v-if="showMore && question.comprehension.body.length > 520"
              class="read-less"
              @click="showMore = false"
            >
              Read less
            </div>
          </div>
          <div
            v-if="!showMore && question.comprehension.body.length > 520"
            class="read-more-btn"
          >
            <button @click="showMore = true" class="show-more">
              <span class="expand-svg d-flex">
                <svg
                  width="14"
                  height="14"
                  viewBox="0 0 14 14"
                  fill="none"
