          v-show="question.image"
          class="image-section rounded-5 position-relative mgt-15"
        >
          <img :src="question.image" class="image-preview" alt="image" />
        </div>

        <!-- options a,bc.. -->
        <div
          v-if="question.type === 'multiple'"
          class="options-to-questions-wrapper"
        >
          <div
            class="options-question-item full-width"
            v-for="option in optionsList.filter((e) => e.label)"
            :key="option.option"
          >
