              v-if="question.answer"
              class="circle-option-tick avatar"
              :class="
                option.option.toUpperCase() === question.answer.toUpperCase()
                  ? ' circle-option-tick-correct-active'
                  : ''
              "
              >{{ option.option.toUpperCase() }}</span
            >
