    <p v-else>No availiable prop in component assesment card</p>
  </div>
</template>

<script>
import CustomInput from "./CustomTextInput/TextInput.vue";
import { GET_TOPICS_LIST } from "../pages/QuestionCreate/store.module.QuestionCreateModule/constants";
import { mapGetters } from "vuex";
export default {
  name: "AssesmentCard",
  data() {
    return {
      is_card_btn_active: false,
      array: Array,
      data_toggle: `${Date.now()}_${this.question?.id}`,
      optionsList: [],
      showMore: false,
      checked: false,
    };
  },
  components: {
    CustomInput,
  },
  props: {
    question: {
      type: Object,
    },
    showDrop: {
      type: Boolean,
      default: true,
    },
    index: {
      type: Number,
    },
    isIndexed: {
      type: Boolean,
      default: false,
    },
    isPreview: {
      type: Boolean,
      default: false,
    },
    topicName: {
      type: String,
      default: "LCM",
    },
    check: {
      type: Boolean,
      default: false,
    },
  },

  computed: {
    ...mapGetters([GET_TOPICS_LIST]),

    topicExist() {
      return Object.keys(this.GET_TOPICS_LIST).length && this.topicName;
    },
  },

  methods: {
    handle_click_away(e) {
      if (e.target.getAttribute("data-toggle") === this.data_toggle) {
        this.is_card_btn_active = !this.is_card_btn_active;
        return;
      }
      this.is_card_btn_active = false;
    },
    customOptionListFactory(question) {
      this.optionsList = [];
      let options = [];
      if (question && Object.keys(question).length > 0) {
        Object.keys(question).some((key) => {
          if (/option_/.test(key)) {
            const keyOption = key.split("");
            options.push({
              option: keyOption[keyOption.length - 1],
              label: question[key],
            });
          }
        });
        this.optionsList = options;
        return;
      }
      return;
    },
  },
  mounted() {
    if (this.question?.type === "multiple") {
      this.customOptionListFactory(this?.question);
    }
  },
  watch: {
    index: {
      handler() {
        console.log("new random index", this.index);
      },
    },

    question: {
      handler() {
        if (this.question?.type === "multiple") {
          this.customOptionListFactory(this?.question);
        }
      },
      deep: true,
      immediate: true,
    },
  },
  created() {
    this.$bus.$on("clearQuestionSelection", () => {
      this.checked = false;
      this.$emit("selectionUpdate", false);
    });

    window.addEventListener("click", this.handle_click_away);
  },
  beforeDestroy() {
    window.removeEventListener("click", this.handle_click_away);
  },
  updated() {
    window?.MathJax?.typeset();
  },
};
</script>

